from langchain_core.messages import SystemMessagefrom langgraph.prebuilt import ToolNode# Step 1: Generate an AIMessage that may include a tool-call to be sent.def query_or_respond(state: MessagesState):    """Generate tool call for retrieval or respond."""    llm_with_tools = llm.bind_tools([retrieve])    response = llm_with_tools.invoke(state["messages"])    # MessagesState appends messages to state instead of overwriting    return {"messages": [response]}# Step 2: Execute the retrieval.tools = ToolNode([retrieve])# Step 3: Generate a response using the retrieved content.def generate(state: MessagesState):    """Generate answer."""    # Get generated ToolMessages    recent_tool_messages = []    for message in reversed(state["messages"]):        if message.type == "tool":            recent_tool_messages.append(message)        else:            break    tool_messages = recent_tool_messages[::-1]    # Format into prompt    docs_content = "

".join(doc.content for doc in tool_messages)    system_message_content = (        "You are an assistant for question-answering tasks. "        "Use the following pieces of retrieved context to answer "        "the question. If you don't know the answer, say that you "        "don't know. Use three sentences maximum and keep the "        "answer concise."        "

"        f"{docs_content}"    )    conversation_messages = [        message        for message in state["messages"]        if message.type in ("human", "system")        or (message.type == "ai" and not message.tool_calls)    ]    prompt = [SystemMessage(system_message_content)] + conversation_messages    # Run    response = llm.invoke(prompt)    return {"messages": [response]}
